import React from "react";
import { useDispatch } from "react-redux";
import { Grid } from "@mui/material";
import CustomButton from "../common/CustomButton";
import { textConst } from "../../common/textConst";
import styles from "../styles/addButton.module.scss";
import actionConstants from "../../redux/actionsConstants";

const AddButton = ({ inputValue, setInputValue }) => {
  const dispatch = useDispatch();

  const handleAddTodo = () => {
    const id = 0;
    const payload = {
      id: id + 1,
      inputValue,
    };
    dispatch({ type: actionConstants.GET_TODO_REQUEST, payload });
    setInputValue("");
    console.log("add todo button");
  };

  return (
    <Grid className={styles.add_button}>
      <CustomButton
        color="success"
        size="lg"
        variant="soft"
        text={textConst.addTodo}
        onClick={handleAddTodo}
      />
    </Grid>
  );
};

export default AddButton;
