import actionConstants from "./actionsConstants";

export const getTodo = () => ({
  type: actionConstants.GET_TODO,
});

export const addTodo = (data) => ({
  type: actionConstants.ADD_TODO,
  payload: {
    todo: data,
  },
});

export const updateTodo = (todoId, data) => ({
  type: actionConstants.UPDATE_TODO,
  payload: {
    todoId: todoId,
    data,
  },
});

export const deleteTodo = (todoId) => ({
  type: actionConstants,
  payload: {
    todoId,
  },
});
